import React, { Component } from "react";
import ReactDOM from "react-dom";
import { throwStatement } from "@babel/types";

class Main extends Component {
  state = {
    fileContent: "",
    fileContentClean: "",
    tagbox: "Enter text to mark"
  };

  hadleClickOnUpload = () => {
    if (window.File && window.FileReader && window.FileList && window.Blob) {
      var preview = document.getElementById("temporaryPlace");
      var file = document.querySelector("input[type=file]").files[0];
      var reader = new FileReader();
      var textHolder = "File Content hasnot set";

      var textFile = /text.*/;
      if (file.type.match(textFile)) {
        reader.onload = function(event) {
          preview.innerHTML = event.target.result;
        };
      } else {
        preview.innerHTML =
          "<span class='error'>It doesn't seem to be a text file!</span>";
      }
      reader.readAsText(file);
    } else {
      alert("Your browser is too old to support HTML5 File API");
    }

    textHolder = preview.innerHTML;
    this.setState({ fileContent: textHolder });
  };
  //if (file.type.match(textFile)) {
  //  reader.onload = function(event) {
  //    preview.innerHTML = event.target.result;
  //    //this.setState({ fileContent: event.target.result });
  //  };
  //}

  setTags = () => {
    let text = this.refresh();
    let higlight = "<[^<]+>[^<]+</[^<]+>"; //this.state.tagbox;
    let regexHiglight = RegExp(higlight);
    let delelteTags = "<[^<]+>";
    let regexDelelteTags = RegExp(delelteTags);
    let par;

    //Split on higlight term and include term into parts, ignore case
    let parts = text.split(new RegExp(`(${higlight})`, "gi"));
    let pars = [parts.length];
    for (let index = 0; index < parts.length; index++) {
      pars[index] = parts[index].split(new RegExp(`(${delelteTags})`, "gi"));
      console.log("parts[" + index + "]=" + parts[index]);
      console.log("pars[" + index + "].length=" + pars[index].length);
      console.log("pars[" + index + "]=" + pars[index]);
    }

    let bla = (
      <div>
        {" "}
        {parts.map((part, i) => (
          <span
            key={i}
            style={
              regexHiglight.test(part.toLowerCase())
                ? //part.toLowerCase() === higlight.toLowerCase()
                  { fontWeight: "bold", backgroundColor: "yellow" }
                : {}
            }
          >
            {pars[i].map((par, j) => (
              <span>
                key={i}
                {regexDelelteTags.test(par.toLowerCase()) ? {} : { par }}
              </span>
            ))}
            )}
          </span>
        ))}{" "}
      </div>
    );
    this.setState({ fileContent: bla });
  };

  highlightText = () => {
    let textHolder = this.refresh();
    this.setState({
      fileContent: this.getHighlightedText(textHolder, this.state.tagbox)
    });
  };

  handleChange = event => {
    this.setState({ tagbox: event.target.value });
  };

  getHighlightedText = (data, mark) => {
    //console.log(this);
    let text = data;
    let higlight = mark; //this.state.tagbox;
    // Split on higlight term and include term into parts, ignore case
    let parts = text.split(new RegExp(`(${higlight})`, "gi"));
    let bla = (
      <div>
        {" "}
        {parts.map((part, i) => (
          <span
            key={i}
            style={
              part.toLowerCase() === higlight.toLowerCase()
                ? { fontWeight: "bold", backgroundColor: "yellow" }
                : {}
            }
          >
            {part}
          </span>
        ))}{" "}
      </div>
    );
    return bla;
  };

  refresh = () => {
    // Check wether the information stored at a temporary place or at the application state.
    // Get the information from the temporary place.
    var preview = document.getElementById("temporaryPlace");
    var textHolder;

    // Checks if the information is in the temporary place.
    if (preview.innerHTML != "") {
      textHolder = preview.innerHTML;
      preview.innerHTML = "";
    } else {
      textHolder = this.state.fileContentClean;
    }

    this.setState({
      fileContentClean: textHolder
    });

    return textHolder;
  };

  render() {
    //<input type="file" onChange={this.hadleClickOnUpload} />
    //<div> the file chosen </div>
    return (
      <React.Fragment>
        <h1>Text editor</h1>
        <input type="file" onChange={this.hadleClickOnUpload}></input>
        <br></br>
        <div id="temporaryPlace"></div>
        <div id="light">{this.state.fileContent}</div>
        <input
          id="tags"
          type="text"
          height="200"
          width="200"
          value={this.state.tagbox}
          onChange={this.handleChange}
        />
        <br></br>
        <button onClick={this.highlightText}> Highlight</button>
        //<button onClick={this.setTags}> Set tags</button>
        <br></br>
      </React.Fragment>
    );
  }
}
