import React, { Component } from "react";
import ReactDOM from "react-dom";

class Main extends Component {
  state = {
    fileContent: "Choose text file"
  };

  hadleClickOnUpload = () => {
    if (window.File && window.FileReader && window.FileList && window.Blob) {
      var preview = document.getElementById("fileContent");
      var file = document.querySelector("input[type=file]").files[0];
      var reader = new FileReader();

      var textFile = /text.*/;

      if (file.type.match(textFile)) {
        reader.onload = function(event) {
          preview.innerHTML = event.target.result;
        };
      } else {
        preview.innerHTML =
          "<span class='error'>It doesn't seem to be a text file!</span>";
      }
      reader.readAsText(file);
    } else {
      alert("Your browser is too old to support HTML5 File API");
    }
  };

  render() {
    var divStyle = {
      color: "white"
      //backgroundImage: 'url(' + imgUrl + ')',
      //WebkitTransition: 'all', // note the capital 'W' here
      //msTransition: 'all' // 'ms' is the only lowercase vendor prefix
    };

    return (
      <React.Fragment>
        <h1>Text editor</h1>
        <input type="file" onChange={this.hadleClickOnUpload} />
        <div id="show-text">Choose text File</div>
        <input
          id="fileContent"
          height="1000"
          width="200"
          type="file"
          value={this.state.fileContent}
          //value={this.state.value}
          //onChange={this.handleChange}
        />
        <input
          id="tagsPlace"
          height="200"
          width="200"
          type="text"
          value={this.state.fileContent}
          //value={this.state.value}
          //onChange={this.handleChange}
        />
        <br></br>
        <button> Upload file</button>
        <button onClick={this.hadleClickOnUpload}> Save file</button>
        <br></br>
      </React.Fragment>
    );
  }
}

export default Main;
