import React, { Component } from "react";
import ReactDOM from "react-dom";
import { throwStatement } from "@babel/types";

class Main extends Component {
  state = {
    fileContent: "",
    tagbox: "Enter text to mark"
  };

  hadleClickOnUpload = () => {
    if (window.File && window.FileReader && window.FileList && window.Blob) {
      var preview = document.getElementById("temporaryPlace");
      var file = document.querySelector("input[type=file]").files[0];
      var reader = new FileReader();
      var textHolder = "File Content hasnot set";

      var textFile = /text.*/;
      if (file.type.match(textFile)) {
        reader.onload = function(event) {
          preview.innerHTML = event.target.result;
        };
      } else {
        preview.innerHTML =
          "<span class='error'>It doesn't seem to be a text file!</span>";
      }
      reader.readAsText(file);
    } else {
      alert("Your browser is too old to support HTML5 File API");
    }

    textHolder = preview.innerHTML;
    this.setState({ fileContent: textHolder });
  };
  //if (file.type.match(textFile)) {
  //  reader.onload = function(event) {
  //    preview.innerHTML = event.target.result;
  //    //this.setState({ fileContent: event.target.result });
  //  };
  //}

  highlightText = () => {
    //this.refresh();
    this.setState({ fileContent: this.getHighlightedText() });
    //var preview = document.getElementById("fileContent");
    //var Component = React.createClass({
    //  _highlightQuery: function(name, query) {
    //    var regex = new RegExp("(" + query + ")", "gi");
    //    return name.replace(regex, "<h1>$1</h1>");
    //  },
    //  render: function() {
    //    var name = "Javascript";
    //    var query = "java";
    //    return (
    //      <div>
    //        <input type="checkbox" /> {this._highlightQuery(name, query)}
    //      </div>
    //    );
    //  }
    //});
  };

  handleChange = event => {
    this.setState({ tagbox: event.target.value });
  };

  getHighlightedText = () => {
    let text = this.state.fileContent;
    let higlight = bla; //this.state.tagbox;
    // Split on higlight term and include term into parts, ignore case
    let parts = text.split(new RegExp(`(${higlight})`, "gi"));
    let bla = (
      <div>
        {" "}
        {parts.map((part, i) => (
          <span
            key={i}
            style={
              part.toLowerCase() === higlight.toLowerCase()
                ? { fontWeight: "bold", backgroundColor: "yellow" }
                : {}
            }
          >
            {part}
          </span>
        ))}{" "}
      </div>
    );
    return bla;
  };

  refresh = () => {
    var preview = document.getElementById("temporaryPlace");
    // console.log(preview.innerHTML);
    if (preview.innerHTML != "") {
      var textHolder = preview.innerHTML;
      this.setState({ fileContent: textHolder });
      preview.innerHTML = "";
    }
    var preview = document.getElementById("temporaryPlace");
  };

  render() {
    //<input type="file" onChange={this.hadleClickOnUpload} />
    //<div> the file chosen </div>
    return (
      <React.Fragment>
        <h1>Text editor</h1>
        <input type="file" onChange={this.hadleClickOnUpload}></input>
        <br></br>
        <button onClick={this.highlightText}> Highlight</button>
        <div id="temporaryPlace"></div>
        <div id="light">
          <span> {this.state.fileContent} </span>
        </div>
        <input
          id="tags"
          type="text"
          height="200"
          width="200"
          value={this.state.tagbox}
          onChange={this.handleChange}
        />
        <br></br>
        <button onClick={this.refresh}> Refresh</button>
        <br></br>
      </React.Fragment>
    );
  }
}

//<input
//id="fileContent"
//height="1000"
//width="200"
//value={this.state.value}
//onChange={this.handleChange}
///>

//<button onClick={this.hadleClickOnUpload}> Upload file</button>
//       <button> Save file</button>
//       <br></br>
//<input
//          id="tagsPlace"
//          height="200"
//          width="200"
//          type="text"
//          value={this.state.fileContent}
//value={this.state.value}
//onChange={this.handleChange}
//        />
export default Main;
